// src/Login.js

import React from 'react';
import { Container, Grid, Paper, TextField, Button, Typography } from '@mui/material';

const Login = () => {
  return (
    <Container
      component="main"
      sx={{
        height: '100vh', 
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <Grid container>
        {/* Section gauche avec l'image */}
        <Grid
          item
          xs={12}
          md={6}
          sx={{
            backgroundImage: 'url(https://source.unsplash.com/random)', // Remplacez par l'URL de votre image
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        
        {/* Section droite avec le formulaire */}
        <Grid item xs={12} md={6}>
          <Paper
            sx={{
              padding: 4,
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              height: '100%',
            }}
          >
            <Typography component="h1" variant="h5">
              Connexion
            </Typography>
            <form noValidate style={{ width: '100%' }}>
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                label="Adresse e-mail"
                autoComplete="email"
                autoFocus
              />
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                label="Mot de passe"
                type="password"
                autoComplete="current-password"
              />
              <Button type="submit" fullWidth variant="contained" color="primary">
                Se connecter
              </Button>
            </form>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Login;
