import React from 'react';
import { Container, Grid, Paper, TextField, Button, Typography } from '@mui/material';

// Styles
const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(https://source.unsplash.com/random)', // Remplacez par l'URL de votre image
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    padding: theme.spacing(4),
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  form: {
    width: '100%', // Prend toute la largeur
  },
}));

const Login = () => {
  const classes = useStyles();

  return (
    <Container component="main" className={classes.root}>
      <Grid container>
        <Grid item xs={12} md={6} className={classes.image} />
        <Grid item xs={12} md={6}>
          <Paper className={classes.paper}>
            <Typography component="h1" variant="h5">
              Connexion
            </Typography>
            <form className={classes.form} noValidate>
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                label="Adresse e-mail"
                autoComplete="email"
                autoFocus
              />
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                label="Mot de passe"
                type="password"
                autoComplete="current-password"
              />
              <Button type="submit" fullWidth variant="contained" color="primary">
                Se connecter
              </Button>
            </form>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Login;
